SELECT Dives.ID AS Dives_ID, Dives.*, Basic.*, Conditions.*, Details.*, Certification.*, Weather.*, Advanced.*, Location.*, Get_SI.Surface_Interval_Adjusted2, (SELECT COUNT(*) FROM Dives WHERE d.ID>=ID) AS RowNum, Depth_Unit.Unit, Advanced_Pressure_Unit.Unit, Advanced_Tank_Unit.Unit, Temperature_Unit.Unit, Wave_Unit.Unit, Altitude_Unit.Unit, Visibility_Unit.Unit, Weight_Unit.Unit, Pressure_Unit.Unit, Max_Dpeth_Unit.Unit, Tank_Size_Unit.Unit, Saftey_Stop_Depth_Unit.Unit
FROM (((((((((((((Get_SI RIGHT JOIN ((Location RIGHT JOIN (((((Certification RIGHT JOIN Dives ON Certification.ID = Dives.Certification_ID) LEFT JOIN Advanced ON Dives.ID = Advanced.Dive_ID) LEFT JOIN Basic ON Dives.ID = Basic.Dive_ID) LEFT JOIN Conditions ON Dives.ID = Conditions.Dive_ID) LEFT JOIN Details ON Dives.ID = Details.Dive_ID) ON Location.ID = Dives.Location_ID) LEFT JOIN Weather ON Dives.ID = Weather.Dive_ID) ON Get_SI.ID = Dives.ID) LEFT JOIN Dives AS d ON Dives.ID = d.ID) LEFT JOIN Units AS Depth_Unit ON Advanced.Depth_1_Unit_ID = Depth_Unit.ID) LEFT JOIN Units AS Advanced_Pressure_Unit ON Advanced.Pressure_1_Start_Unit_ID = Advanced_Pressure_Unit.ID) LEFT JOIN Units AS Advanced_Tank_Unit ON Advanced.Tank_1_Size_Unit_ID = Advanced_Tank_Unit.ID) LEFT JOIN Units AS Weight_Unit ON Basic.Weight_Unit_ID = Weight_Unit.ID) LEFT JOIN Units AS Pressure_Unit ON Basic.Pressure_Start_Unit_ID = Pressure_Unit.ID) LEFT JOIN Units AS Max_Dpeth_Unit ON Basic.Max_Depth_Unit_ID = Max_Dpeth_Unit.ID) LEFT JOIN Units AS Saftey_Stop_Depth_Unit ON Basic.Safety_Stop_Depth_Unit_ID = Saftey_Stop_Depth_Unit.ID) LEFT JOIN Units AS Tank_Size_Unit ON Basic.Tank_Size_Unit_ID = Tank_Size_Unit.ID) LEFT JOIN Units AS Temperature_Unit ON Conditions.Temperature_Air_Unit_ID = Temperature_Unit.ID) LEFT JOIN Units AS Wave_Unit ON Conditions.Wave_Unit_ID = Wave_Unit.ID) LEFT JOIN Units AS Altitude_Unit ON Conditions.Altitude_Unit_ID = Altitude_Unit.ID) LEFT JOIN Units AS Visibility_Unit ON Conditions.Visibility_Unit_ID = Visibility_Unit.ID
ORDER BY Dives.Time_In;
